mapper.update:
update whitegoods set
	w_name = #{w_name},
	w_price = #{w_price},
	cate_no = #{cate_no},
	w_content = #{w_content},
	w_thumbimg = #{w_thumbimg}
where w_no = #{w_no}
and w_seller = #{w_seller}

update whitegoods set
	p_readcount = p_readcount + 1
where w_no = #{w_no}

mapper.select:
select img_name from product_images
where p_no = #{p_no}
order by img_no desc
