mapper.update:
update whitegoods set
	w_name = #{w_name},
	w_price = #{w_price},
	cate_no = #{cate_no},
	w_content = #{w_content},
	w_thumbimg = #{w_thumbimg}
where w_no = #{w_no}
and w_seller = #{w_seller}

mapper.delete:
delete from whitegoods
where w_no = #{w_no}
and w_seller = #{w_seller}
